import React, {Component} from 'react';
import '../App.css';

class StringCalculator extends Component {
    constructor(props){
        super(props)
        this.state = {
            showResult:false,
            sumValue: '',
            textValue: ""
        }
    }

    add(){
        let sumValue = 0
        let textValue = this.state.textValue
        if(!textValue){
            sumValue = 0
        }
        textValue = '//[***]\n1***2***3'
        let delimiter = /[/n,;]+/
        const match = textValue.match(/^\/\/(\[.*\])[\n](.*)/);
        if(match){
            console.log("in if")
            textValue = match[2];

            // let delimiters = [...match[1].matchAll(/\[(.*?)\]/g)].map(m => m[1]);
            // let regexPattern = delimiters.map(d => d.replace(/[-\/\\^$*+?.()|[\]{}]/g, '\\$&')).join('|');
            // delimiter = new RegExp(regexPattern);

            // let customDelimiter = match[1].slice(1, -1).replace(/[-[\]{}()*+?.,\\^$|#\s]/g, '\\$&');
            // delimiter = new RegExp(customDelimiter + "|/\n");

            const delimiterPart = match[1];
            const delimiters = delimiterPart.match(/\[(.*?)\]/g).map(d => d.slice(1, -1));
            delimiter = new RegExp(delimiters.map(d => d.replace(/[-[\]{}()*+?.,\\^$|#\s]/g, '\\$&')).join('|'));
            console.log(delimiter)
        }
        let numArray = textValue.split(delimiter).map(num => parseFloat(num) || 0);
        const negatives = numArray.filter(num => num < 0);
        if (negatives.length) {
            alert(`Negative numbers not allowed: ${negatives.join(", ")}`);
            return;
        }
        numArray = numArray.filter((x) => x < 1000)
        sumValue = numArray.reduce((sum, num) => sum + num, 0);
        this.setState({sumValue, showResult: true})    
    }

    render() {
      return (
        <div className="App">
            <header className="App-header">
                <input type="text" placeholder="Type your string.." value={this.state.textValue} onChange={(e)=> this.setState({textValue: e.target.value})} />
                <button type="submit" onClick={this.add.bind(this)}>Submit</button>
                {
                    this.state.showResult ? 
                    <p>Sum is: {this.state.sumValue}</p> : null
                }
            </header>
        </div>
      );
    }
}

export default StringCalculator;
